import React from 'react'
import { OTPInputContext } from './input-otp'

export function useOTPInputContext() {
  const context = React.useContext(OTPInputContext)
  if (context === null) {
    throw new Error('useOTPInputContext must be used within a OTPInputProvider')
  }
  return context
}
export function useInputOTPInit(
  value?: string,
  onValueChange?: (value: string) => void,
  pattern: RegExp = /^[\w\d\p{P}\p{S}]$/u, // default if not provided
) {
  const inputsRef = React.useRef<HTMLInputElement[]>([])
  const wrapperRef = React.useRef<HTMLDivElement>(null)

  React.useEffect(() => {
    const html = document.documentElement
    const inputs = Array.from(
      wrapperRef?.current?.querySelectorAll('input[duck-input-otp-slot]') as never as HTMLInputElement[],
    )
    const valueChunks = value?.split('')
    inputsRef.current = inputs

    for (let i = 0; i < inputsRef.current.length; i++) {
      const item = inputsRef.current[i] as HTMLInputElement
      item.value = valueChunks?.[i] ?? ''
      item.setAttribute('aria-label', `Digit ${i + 1}`)

      item.addEventListener('keydown', (e) => {
        // navigation keys
        if (
          e.key === 'Backspace' ||
          (e.key === 'ArrowLeft' && html.getAttribute('dir') === 'ltr') ||
          (e.key === 'ArrowRight' && html.getAttribute('dir') === 'rtl')
        ) {
          setTimeout(() => inputs[i - 1]?.focus(), 0)
        }

        if (
          (e.key === 'ArrowLeft' && html.getAttribute('dir') === 'rtl') ||
          (e.key === 'ArrowRight' && (html.getAttribute('dir') === 'ltr' || html.getAttribute('dir') === null))
        ) {
          setTimeout(() => inputs[i + 1]?.focus(), 0)
        }

        // skip special keys
        if (
          e.metaKey ||
          e.ctrlKey ||
          e.altKey ||
          ['ArrowLeft', 'ArrowRight', 'Backspace', 'Enter', 'Tab', 'ArrowUp', 'ArrowDown'].includes(e.key)
        ) {
          return
        }

        // validate input with pattern
        if (!pattern.test(e.key)) {
          e.preventDefault()
          return
        }

        item.value = e.key
        setTimeout(() => inputs[i + 1]?.focus(), 0)
        onValueChange?.(inputs.map((input) => input.value).join(''))
      })
    }
  }, [inputsRef, pattern])

  return { inputsRef, wrapperRef }
}

'use client'

import { cn } from '@gentleduck/libs/cn'
import { Dot } from 'lucide-react'
import * as React from 'react'
import { useInputOTPInit } from './input-otp.hooks'

export const REGEXP_ONLY_DIGITS_AND_CHARS = /^[\w\d\p{P}\p{S}]$/u
export const REGEXP_ONLY_DIGITS = /^[0-9]$/
export const OTPInputContext = React.createContext<{
  value?: string
  wrapperRef: React.RefObject<HTMLDivElement | null>
  inputsRef: React.RefObject<HTMLInputElement[]>
} | null>(null)

function InputOTP({
  className,
  children,
  value,
  onValueChange,
  pattern,
  ref,
  'aria-label': ariaLabel = 'One-time password input',
  ...props
}: Omit<React.HTMLProps<HTMLDivElement>, 'pattern'> & {
  value?: string
  onValueChange?: (value: string) => void
  pattern?: RegExp
}) {
  const { inputsRef, wrapperRef } = useInputOTPInit(value, onValueChange, pattern)

  return (
    <OTPInputContext.Provider
      value={{
        value,
        wrapperRef,
        inputsRef,
      }}>
      <div
        ref={wrapperRef}
        className={cn('flex items-center gap-2 disabled:cursor-not-allowed has-[:disabled]:opacity-50', className)}
        role="group"
        aria-label={ariaLabel}
        {...props}
        duck-input-otp="">
        {children}
      </div>
    </OTPInputContext.Provider>
  )
}

const InputOTPGroup = ({ className, ref, ...props }: React.ComponentPropsWithRef<'div'>) => {
  return (
    <div
      ref={ref}
      className={cn('flex items-center', className)}
      role="group"
      aria-label="OTP group"
      {...props}
      duck-input-otp-group=""
    />
  )
}

const InputOTPSlot = ({ className, ref, ...props }: React.ComponentPropsWithRef<'input'>) => {
  return (
    <input
      ref={ref}
      className={cn(
        'relative h-10 w-10 border-input border-y border-r text-center text-sm transition-all first:rounded-l-md first:border-l last:rounded-r-md focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
        className,
      )}
      aria-required="true"
      aria-invalid="false"
      maxLength={1}
      duck-input-otp-slot=""
      {...props}
    />
  )
}

const InputOTPSeparator = ({
  ref,
  customIndicator,
  ...props
}: React.ComponentPropsWithRef<'div'> & {
  customIndicator?: React.ReactNode
}) => {
  return (
    <div ref={ref} role="presentation" aria-hidden="true" {...props} duck-input-otp-separator="">
      {customIndicator ? customIndicator : <Dot />}
    </div>
  )
}

export { InputOTP, InputOTPGroup, InputOTPSlot, InputOTPSeparator }
