import React from 'react'
import { RadioGroupContext } from './radio-group'

export function useRadioGroupContext() {
  const context = React.useContext(RadioGroupContext)
  if (!context) {
    throw new Error('useRadioGroupContext must be used within a RadioGroup')
  }
  return context
}

export function useHandleRadioClick(defaultValue?: string, value?: string, onValueChange?: (value: string) => void) {
  const wrapperRef = React.useRef<HTMLUListElement>(null)
  const selectedItemRef = React.useRef<HTMLLIElement | null>(null)
  const itemsRef = React.useRef<HTMLLIElement[]>([])

  React.useEffect(() => {
    itemsRef.current = Array.from(wrapperRef.current?.querySelectorAll('[duck-radio-item]') ?? []) as HTMLLIElement[]

    // Handle click on each item
    function handleItemClick(itemInput: HTMLInputElement, item: HTMLLIElement) {
      for (let i = 0; i < itemsRef.current.length; i++) {
        const item = itemsRef.current[i] as HTMLLIElement
        const itemInput = item.querySelector('input') as HTMLInputElement
        item.removeAttribute('aria-checked')
        onValueChange?.(item.id)
        if (itemInput) {
          itemInput.removeAttribute('aria-checked')
          itemInput.checked = false
        }
      }

      handleItem(itemInput, item)
    }

    // Handle item styles
    function handleItem(itemInput: HTMLInputElement, item: HTMLLIElement) {
      if (itemInput) {
        item.setAttribute('aria-checked', 'true')
        itemInput?.setAttribute('aria-checked', 'true')
        itemInput.checked = true
      }
    }

    // Handle all the items
    for (let i = 0; i < itemsRef.current.length; i++) {
      const item = itemsRef.current[i] as HTMLLIElement
      const itemInput = item.querySelector('input') as HTMLInputElement
      const itemLabel = item.querySelector('label') as HTMLLabelElement

      // Handle default value
      if (defaultValue === item.id) {
        handleItem(itemInput, item)
      }

      itemInput?.addEventListener('click', () => handleItemClick(itemInput, item))
      itemLabel.addEventListener('click', () => handleItemClick(itemInput, item))
    }
  }, [wrapperRef])

  return {
    wrapperRef,
    selectedItemRef,
    itemsRef,
  }
}

'use client'

import { cn } from '@gentleduck/duck-libs/cn'
import { AnimVariants, checkersStylePattern } from '@gentleduck/motion/anim'
import { useSvgIndicator } from '@gentleduck/primitives/checkers'
import * as React from 'react'
import { Label } from '../label'
import { useHandleRadioClick } from './radio-group.hooks'

export interface RadioGroupContextType {
  value: string
  onValueChange: (value: string) => void
  wrapperRef: React.RefObject<HTMLUListElement | null>
  itemsRef: React.RefObject<HTMLLIElement[]>
  selectedItemRef: React.RefObject<HTMLLIElement | null>
}

export const RadioGroupContext = React.createContext<RadioGroupContextType | null>(null)

function Radio({
  className,
  indicator,
  checkedIndicator,
  ref,
  style,
  ...props
}: React.HTMLProps<HTMLInputElement> & { indicator?: React.ReactElement; checkedIndicator?: React.ReactElement }) {
  const { indicatorReady, checkedIndicatorReady, inputStyle, SvgIndicator } = useSvgIndicator({
    indicator,
    checkedIndicator,
  })

  return (
    <>
      <input
        type="radio"
        ref={ref}
        duck-radio=""
        style={{ ...style, ...inputStyle }}
        className={cn(
          checkersStylePattern({
            type: 'radio',
            indicatorState:
              indicatorReady && checkedIndicatorReady
                ? 'both'
                : indicatorReady
                  ? 'indicatorReady'
                  : checkedIndicatorReady
                    ? 'checkedIndicatorReady'
                    : 'default',
          }),
          AnimVariants({ overlay: 'nothing', pseudo: 'animate' }),
          className,
        )}
        {...props}
      />
      <SvgIndicator className="sr-only" />
    </>
  )
}

function RadioGroup({
  className,
  children,
  value,
  onValueChange,
  defaultValue,
  ...props
}: React.HTMLProps<HTMLUListElement> & {
  value?: string
  onValueChange?: (value: string) => void
  defaultValue?: string
}) {
  const { selectedItemRef, itemsRef, wrapperRef } = useHandleRadioClick(defaultValue, value, onValueChange)

  return (
    <RadioGroupContext.Provider
      value={{
        value: '',
        onValueChange: () => {},
        wrapperRef,
        itemsRef,
        selectedItemRef,
      }}>
      <ul duck-radio-group="" className={cn('flex flex-col', className)} ref={wrapperRef} role="radiogroup" {...props}>
        {children}
      </ul>
    </RadioGroupContext.Provider>
  )
}

function RadioGroupItem({
  className,
  children,
  customIndicator,
  value,
  ...props
}: Omit<React.HTMLProps<HTMLLIElement>, 'value'> & { customIndicator?: React.ReactNode; value: string }) {
  return (
    <li
      id={value}
      duck-radio-item=""
      role="presentation"
      className={cn(
        'relative flex items-center gap-2 [&>#radio-indicator]:opacity-0 [&[aria-checked=true]>#radio-indicator]:opacity-100',
        className,
      )}
      {...props}>
      {customIndicator && <span id="radio-indicator">{customIndicator}</span>}
      <Radio id={value} className={cn(customIndicator?.toString() && 'hidden')} />
      <Label duck-radio-label="" className="font-normal text-sm" htmlFor={value}>
        {children}
      </Label>
    </li>
  )
}

export { Radio, RadioGroup, RadioGroupItem }
