import { cva } from '@gentleduck/variants'

export const AnimSheetVariants = cva(
  '!fixed pointer-events-all relative z-50 flex flex-col gap-4 overflow-hidden bg-background p-6 shadow-lg outline-hidden transition ease-in-out data-[open=false]:animate-out data-[open=false]:animate-out data-[open=true]:animate-in data-[open=true]:animate-in sm:max-w-sm',

  {
    variants: {
      side: {
        left: 'data-[open=false]:slide-out-to-left data-[open=true]:slide-in-from-left inset-y-0 left-0 h-full h-screen max-h-screen w-3/4 w-fit border-r border-r data-[open=false]:duration-400 data-[open=true]:duration-300',

        right:
          'data-[open=false]:slide-out-to-right data-[open=true]:slide-in-from-right inset-y-0 right-0 h-full h-screen max-h-screen w-3/4 w-fit border-l border-l data-[open=false]:duration-400 data-[open=true]:duration-500',

        top: 'data-[open=false]:slide-out-to-top data-[open=true]:slide-in-from-top inset-x-0 top-0 h-3/4 h-fit max-h-screen w-full border-b border-b data-[open=false]:duration-400 data-[open=true]:duration-300',

        bottom:
          'data-[open=false]:slide-out-to-bottom data-[open=true]:slide-in-from-bottom inset-x-0 bottom-0 h-3/4 h-fit max-h-screen w-full border-t border-t data-[open=false]:duration-400 data-[open=true]:duration-800',
      },
    },
    defaultVariants: {
      side: 'right',
    },
  },
)

'use client'

import { cn } from '@gentleduck/libs/cn'
import { AnimVariants } from '@gentleduck/motion/anim'
import SheetPrimitive, { useSheetContext } from '@gentleduck/primitives/sheet'
import { VariantProps } from '@gentleduck/variants'
import { X } from 'lucide-react'
import type React from 'react'
import { AnimSheetVariants } from './sheet.constants'

function Sheet({ closeButton = true, ...props }: React.ComponentPropsWithoutRef<typeof SheetPrimitive.Root>) {
  return <SheetPrimitive.Root closeButton={closeButton} {...props} />
}

function SheetTrigger({ children, ...props }: React.ComponentPropsWithoutRef<typeof SheetPrimitive.Trigger>) {
  return <SheetPrimitive.Trigger {...props}>{children}</SheetPrimitive.Trigger>
}

export function SheetCloseX({
  ref,
  size = 16,
  children,
  className,
  ...props
}: React.HTMLProps<HTMLButtonElement> & {
  size?: number
}): React.JSX.Element {
  const { setOpen } = useSheetContext()

  return (
    <button
      {...props}
      ref={ref}
      type="button"
      aria-label="close"
      className={cn(
        'absolute end-3 top-3 size-4 cursor-pointer rounded text-accent-foreground opacity-70 transition-all hover:opacity-100',
        className,
      )}
      onClick={() => setOpen?.(false)}>
      {children ?? <X aria-hidden size={size} />}
    </button>
  )
}

function SheetContent({
  children,
  className,
  side = 'right',
  ...props
}: React.ComponentPropsWithRef<typeof SheetPrimitive.Content> &
  VariantProps<typeof AnimSheetVariants>): React.JSX.Element {
  return (
    <SheetPrimitive.Portal>
      <SheetPrimitive.Overlay className={cn(AnimVariants())}>
        <SheetPrimitive.Content
          SheetClose={SheetCloseX}
          className={cn(AnimVariants(), AnimSheetVariants({ side }), className)}
          {...props}>
          {children}
        </SheetPrimitive.Content>
      </SheetPrimitive.Overlay>
    </SheetPrimitive.Portal>
  )
}

function SheetHeader({
  className,
  ref,
  ...props
}: React.ComponentPropsWithRef<typeof SheetPrimitive.Heading>): React.JSX.Element {
  return (
    <SheetPrimitive.Heading
      ref={ref}
      className={cn('flex flex-col gap-1.5 text-left rtl:text-right', className)}
      {...props}
    />
  )
}

function SheetFooter({ className, ref, ...props }: React.HTMLProps<HTMLDivElement>): React.JSX.Element {
  return <div ref={ref} className={cn(`flex justify-end gap-2 sm:flex-row sm:justify-end`, className)} {...props} />
}

function SheetTitle({
  className,
  ref,
  ...props
}: React.ComponentPropsWithRef<typeof SheetPrimitive.Title>): React.JSX.Element {
  return (
    <SheetPrimitive.Title
      ref={ref}
      className={cn('font-semibold text-lg leading-none tracking-tight', className)}
      {...props}
    />
  )
}

const SheetDescription = ({
  className,
  ref,
  ...props
}: React.ComponentPropsWithRef<typeof SheetPrimitive.Description>): React.JSX.Element => (
  <SheetPrimitive.Description ref={ref} className={cn('text-muted-foreground text-sm', className)} {...props} />
)

const SheetClose = SheetPrimitive.Close

export { Sheet, SheetTrigger, SheetContent, SheetHeader, SheetFooter, SheetTitle, SheetDescription, SheetClose }
