import { cva } from '@gentleduck/variants'

export const buttonGroupVariants = cva(
  "flex w-fit items-stretch has-[>[data-slot=button-group]]:gap-2 [&>*]:focus-visible:relative [&>*]:focus-visible:z-10 has-[select[aria-hidden=true]:last-child]:[&>[data-slot=select-trigger]:last-of-type]:rounded-r-md [&>[data-slot=select-trigger]:not([class*='w-'])]:w-fit [&>input]:flex-1",
  {
    defaultVariants: {
      orientation: 'horizontal',
    },
    variants: {
      orientation: {
        horizontal: `
[&>:is(button,input):not(:first-child)]:rounded-l-none
[&>:is(button,input)]:rounded-r-none
[&>:is(button,input):nth-last-child(2):has(+span[aria-hidden])]:!rounded-r-md
[&>:is(button,input):last-child]:!rounded-r-md
[&>:is(button,input):not(:first-child)]:border-l-0
`,
        vertical: `
flex-col
[&>*:not(:first-child)]:rounded-t-none
[&>*:not(:last-child)]:rounded-b-none
[&>*:not(:first-child)]:border-t-0
`,
      },
    },
  },
)

import { cn } from '@gentleduck/libs/cn'
import { type VariantProps } from '@gentleduck/variants'
import { Slot } from '@radix-ui/react-slot'
import { Separator } from '../separator'
import { buttonGroupVariants } from './button-group.constants'

function ButtonGroup({
  className,
  orientation = 'horizontal',
  ...props
}: React.ComponentProps<'div'> & VariantProps<typeof buttonGroupVariants>) {
  return (
    <div
      className={cn(buttonGroupVariants({ orientation }), className)}
      data-orientation={orientation}
      data-slot="button-group"
      role="group"
      {...props}
    />
  )
}

function ButtonGroupText({
  className,
  asChild = false,
  ...props
}: React.ComponentProps<'div'> & {
  asChild?: boolean
}) {
  const Comp = asChild ? Slot : 'div'

  return (
    <Comp
      className={cn(
        "flex items-center gap-2 rounded-md border bg-muted px-4 font-medium text-sm shadow-xs [&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none",
        className,
      )}
      {...props}
    />
  )
}

function ButtonGroupSeparator({
  className,
  orientation = 'vertical',
  ...props
}: React.ComponentProps<typeof Separator>) {
  return (
    <Separator
      className={cn('!m-0 relative self-stretch bg-input data-[orientation=vertical]:h-auto', className)}
      data-slot="button-group-separator"
      orientation={orientation}
      {...props}
    />
  )
}

export { ButtonGroup, ButtonGroupSeparator, ButtonGroupText }
