const chartData = [
  { browser: 'chrome', fill: 'var(--color-chrome)', visitors: 275 },
  { browser: 'safari', fill: 'var(--color-safari)', visitors: 200 },
  { browser: 'firefox', fill: 'var(--color-firefox)', visitors: 187 },
  { browser: 'edge', fill: 'var(--color-edge)', visitors: 173 },
  { browser: 'other', fill: 'var(--color-other)', visitors: 90 },
]

const chartConfig = {
  chrome: {
    color: 'var(--chart-1)',
    label: 'Chrome',
  },
  edge: {
    color: 'var(--chart-4)',
    label: 'Edge',
  },
  firefox: {
    color: 'var(--chart-3)',
    label: 'Firefox',
  },
  other: {
    color: 'var(--chart-5)',
    label: 'Other',
  },
  safari: {
    color: 'var(--chart-2)',
    label: 'Safari',
  },
  visitors: {
    color: 'var(--chart-2)',
    label: 'Visitors',
  },
} satisfies ChartConfig

export default function Component() {
  return (
    <Card>
      <CardHeader>
        <CardTitle>Line Chart - Dots Colors</CardTitle>
        <CardDescription>January - June 2024</CardDescription>
      </CardHeader>
      <CardContent>
        <ChartContainer config={chartConfig}>
          <LineChart
            accessibilityLayer
            data={chartData}
            margin={{
              left: 24,
              right: 24,
              top: 24,
            }}>
